package com.example.userapp.ui.about;


import android.os.Bundle;

import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

import java.util.ArrayList;
import java.util.List;

import androidx.viewpager.widget.ViewPager;

import com.example.userapp.R;

public class AboutFragment extends Fragment {

    private ViewPager viewPager;
    private BranchAdapter adapter;
    private List<BranchModel> list;


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_about, container, false);

        list = new ArrayList<>();
        list.add(new BranchModel(R.drawable.ic_copm, "Computer Science & Engineering", "The BSc. in CSE program under National University aims to provide CSE graduates with the skills and knowledge to take on appropriate professional positions in ..."));
        list.add(new BranchModel(R.drawable.ic_bba, "Bachelor of Business Administration", "The Bachelor of Business Administration (BBA) is a bachelor's degree in business administration. In the United States, the degree is conferred after four years of full-time study in one or more areas of business concentrations."));
        list.add(new BranchModel(R.drawable.ic_thm, "Hospitality & Tourism Management ", "We will prepare you to succeed in a Hospitality & Tourism Management career through Co-op. Study in Downtown Toronto. Our Career Services team will support your co-op and Capstone."));

        adapter = new BranchAdapter( getContext(), list);

        viewPager = view.findViewById(R.id.viewPager);

        viewPager.setAdapter(adapter);

        ImageView imageView = view.findViewById(R.id.college_image);

        Glide.with(getContext())
                .load("data:image/jpeg;base64,")
                .into(imageView);

        return  view;
    }
}
